                                    <input type="search" v-model="search" @input="searchResult" name="search" class="py-2 text-sm  pl-10 outline-none text-gray-900" autocomplete="off">
                                </template>

                                <!-- Drpp down content -->
                                <template #content>
                                    <div v-if="Object.keys(searchResponse).length === 0" class="block px-4 py-2 text-sm leading-5 text-gray-700 hover:bg-gray-100 focus:outline-none focus:bg-gray-100 transition duration-150 ease-in-out">
                                        No results were found
                                    </div>
                                    <div v-else>
                                        <div v-for="searchItem in searchResponse" :key="searchItem.id">
                                            <div>
                                            <jet-dropdown-link :classes="'truncate'" :href="route('client.ticket.show',searchItem.id)">
                                                {{searchItem.subject}}
                                            </jet-dropdown-link>
                                            </div>
                                        </div>
                                    </div>
                                </template>

                            </jet-dropdown>
                            
                        </div>
                    </div>

                    <!-- Right Side -->
                    <div class="hidden md:flex md:items-center md:ml-6">
                        <div class="ml-3 relative">
                            <jet-dropdown align="right" width="48">
                                <!-- Drpp down trigger -->
                                <template #trigger>
                                    <button class="flex items-center text-sm font-medium text-gray-500 hover:text-gray-700 hover:border-gray-300 focus:outline-none focus:text-gray-700 focus:border-gray-300 transition duration-150 ease-in-out">
                                        <div>{{ $page.props.user.name }}</div>
                                        <img class="h-8 w-8 rounded-full mx-2 object-cover" :src="$page.props.user.profile_photo_url" :alt="$page.props.user.name" />

                                        <div class="ml-1">
                                            <svg class="fill-current h-4 w-4" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20">
                                                <path fill-rule="evenodd" d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" clip-rule="evenodd" />
                                            </svg>
                                        </div>
                                    </button>
                                </template>

                                <!-- Drpp down content -->
                                <template #content>
                                    <form @submit.prevent="logout">
                                        <jet-dropdown-link as="button">
                                            Logout
                                        </jet-dropdown-link>
                                    </form>
                                </template>

                            </jet-dropdown>
                        </div>
                    </div>
            </header>
            <section class="p-10">
                <slot></slot>
            </section>
        </main>

        <!-- Modal Portal -->
        <portal-target name="modal" multiple>
        </portal-target>
    </div>
</template>

<script>
    import JetApplicationMark from '@/Jetstream/ApplicationMark'
    import JetDropdown from '@/Jetstream/Dropdown'
    import JetDropdownLink from '@/Jetstream/DropdownLink'
    import JetNavLink from '@/Jetstream/NavLink'
    import JetResponsiveNavLink from '@/Jetstream/ResponsiveNavLink'

    export default {
        components: {
            JetApplicationMark,
            JetDropdown,
            JetDropdownLink,
            JetNavLink,
            JetResponsiveNavLink,
        },

        data() {
            return {
                search: '',
                searchResponse: '',
                showingNavigationDropdown: false,
            }
        },

        methods: {
            searchResult() {
                axios.get(route('client.ticket.ticketList'), {
                    params: {
                        search: this.search,
                    }
                }).then(response => {
                this.searchResponse = {
                    ...response.data
                }  
                });
            },

            logout() {
                axios.post(route('logout').url()).then(response => {
                    window.location = '/';
                })
            },
        }
    }
</script>
